export default {
	insertForm: () => {
		var formobject = {};
		formobject['formheader'] = [];


		if (Ordertype.selectedOptionValue == 'Project') { 
			formobject['formheader'].push({"label":"Project", "value":SelectProject.selectedOptionLabel}); 
		} else {
			formobject['formheader'].push({"label":"Werkbon", "value":Input_Workorder.inputText});
			formobject['formheader'].push({"label":"Klantnaam", "value":Input_WorkorderDescription.inputText});
		}

		formobject['formheader'].push({"label":DatePicker.__evaluation__.evaluatedValues.label, "value":DatePicker.formattedDate});
		formobject['formheader'].push({"label":Input_Trailernumber.__evaluation__.evaluatedValues.label, "value":Input_Trailernumber.inputText});
		formobject['formheader'].push({"label":Input_WorkplaceInspector.__evaluation__.evaluatedValues.label, "value":Input_WorkplaceInspector.inputText});
		formobject['formheader'].push({"label":Input_Client.__evaluation__.evaluatedValues.label, "value":Input_Client.inputText});		
		formobject['formheader'].push({"label":Input_Remark.__evaluation__.evaluatedValues.label, "value":Input_Remark.inputText});	
		formobject['formheader'].push({"label":Input_QuantityEmployees.__evaluation__.evaluatedValues.label, "value":Input_QuantityEmployees.inputText});	
		formobject['formheader'].push({"label":Input_QuantityOthers.__evaluation__.evaluatedValues.label, "value":Input_QuantityOthers.inputText});	

		formobject['questions'] = []
		Table_Questions.updatedRows.forEach(question =>
																				formobject['questions'].push(question.allFields)															
																			 );


		formobject['remarks'] = [];
		Table_Remarks.updatedRows.forEach(remark =>
																			formobject['remarks'].push(remark.allFields)															
																		 );

		return formobject;	
	}
}
