actionConfiguration.body:
() => {
  let questions = {};
  GetFormContent.data.forEach(question => {
    switch (question.answer_type) {
      case 'Tekst':
        questions[question.form_question] = "";
        break;
      case 'Getal':
        questions[question.form_question] = 0;
        break;
      case 'boolean':
        questions[question.form_question] = true;
        break;
      case 'Datum':
        questions[question.form_question] = moment().format('DD-MM-YYYY');
        break;
      case 'Lijst':
        var list_properties = {};
        question.list_properties.split(',').map(property => list_properties[property] = "");
        questions[question.form_question] = [list_properties];
    }
  });
  return questions;
}

